mapper.select:
SELECT id, menu_category_id AS menuCategoryId, store_name AS storeName, store_address AS storeAddress, min_price AS minPrice, tel, owner_id AS ownerId FROM store
AND id = #{id}
AND menu_category_id = #{menuCategoryId}
AND LOWER(store_name) LIKE CONCAT('%', LOWER(#{storeNameLike}), '%')
ORDER BY id DESC
